sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord() || newValue === '') {
      return;
   }

	var user_email = new GlideAjax('GetEmailAddress');
	user_email.addParam('sysparm_name', 'requester_email');
	user_email.addParam('sysparm_user', g_form.getValue('u_requested_for'));
	user_email.getXML(callback);

		function callback(response)
	{
		var answer = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('u_requested_for_email', answer);
	}

   //Type appropriate comment here, and begin script below

}
